@import "../../styles/mixin.scss";

.new-note {
  background-color: #fff;
  padding: 10px 12px;
  border-radius: 2px;
  @include box-shadow(1);
  &_input {
    width: 100%;
    border: none;

    &:focus {
      outline: none;
    }
  }
}
